FROM PTSD_USER
WHERE USERID = #{userid}
AND USERPW = #{userpw}

SELECT USERSEQ, USERID, USERPW, EMAIL, ADDRESS, PHONE, USERROLE, NAME, AVATAR
FROM PTSD_USER
WHERE USERID = #{userid}

SELECT EMAIL
FROM PTSD_USER
WHERE EMAIL = #{email}

SELECT PHONE
FROM PTSD_USER
WHERE PHONE = #{phone}

